import React, { useState, useEffect } from 'react'
import { Instagram, Twitter, Youtube } from 'lucide-react'
import './HomePage.css'
import poster from '../assets/poster.png'
import eventsData from './data.json'

const HomePage = () => {
  const [offsetY, setOffsetY] = useState(0)

  const handleScroll = () => setOffsetY(window.pageYOffset)

  useEffect(() => {
    window.addEventListener('scroll', handleScroll)
    return () => window.removeEventListener('scroll', handleScroll)
  }, [])

  const socialLinks = [
    { icon: <Instagram size={18} />, href: 'https://instagram.com/onkaryadav', color: '#E4405F' },
    { icon: <Youtube size={18} />, href: 'https://www.youtube.com/@boi_onkar', color: '#FF0000' },
    { icon: <Twitter size={18} />, href: 'https://twitter.com/onkaryadav', color: '#1DA1F2' }
  ]

  return (
    <div
      className="homepage"
      style={{
        backgroundImage: `url(${poster})`,
        backgroundPosition: `center ${offsetY * 0.5}px`, // parallax effect
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
        minHeight: '100vh'
      }}
    >
      <div className="overlay"></div>
      <div className="container">

        {/* BookMyShow Cards */}
        <div className="links-section">
          {eventsData.map((event, idx) => (
            <a key={idx} href={event.link} target="_blank" rel="noopener noreferrer" className="link-card">
              <div className="link-content">
                <h3>{event.cityVenue}</h3>
                {event.date && <p>{event.date}{event.time ? ` • ${event.time}` : ''}</p>}
              </div>
            </a>
          ))}
        </div>

        {/* Fixed Social Links Bar */}
        <div className="social-bar">
          {socialLinks.map((link, i) => (
            <a
              key={i}
              href={link.href}
              target="_blank"
              rel="noopener noreferrer"
              className="social-btn"
              style={{ backgroundColor: link.color }}
            >
              {link.icon}
            </a>
          ))}
        </div>

        {/* Footer */}
        <div className="footer">
          <p>Nickey</p>
        </div>

      </div>
    </div>
  )
}

export default HomePage
